class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        const int n = nums.size();
        const int m = queries.size();
        vector<int> res(n, -1);
        for (int p = 0; p < n; ++p) {
            int target = nums[p];
            vector<int> cur(target + 1, 0);
            cur[0] = 1;
            for (int i = 0; i < m; ++i) {
                int l = queries[i][0], r = queries[i][1], v = queries[i][2];
                if (p < l || p > r) continue;
                for (int j = target - 1; j >= 0; --j) {
                    if (cur[j] && j + v <= target) cur[j + v] = 1;
                }
                if (cur[target]) {
                    res[p] = i;
                    break;
                }
            }
        }

        int ans = -1;
        for (int i = 0; i < n; ++i) {
            if (nums[i] == 0) {
                ans = max(ans, 0);
                continue;
            }
            if (res[i] == -1) return -1;
            ans = max(ans, res[i] + 1);
        }
        return ans;
    }
};

