class Solution {
    int traverse(int ind, int curSum, List <int[]> list, int mx, Integer[][] dp){
        if(curSum == 0){
            return mx;
        }

        if(curSum < 0 || ind >= list.size()){
            return Integer.MAX_VALUE;
        }
        if(dp[ind][curSum] != null){
            return dp[ind][curSum];
        }

        int pick = traverse(ind + 1, curSum - list.get(ind)[0], list, Math.max(mx, list.get(ind)[1]), dp);
        int notPick = traverse(ind + 1, curSum, list, mx, dp);
        dp[ind][curSum] = Math.min(pick, notPick);
        return  Math.min(pick, notPick);
    }

    int compute(int x, List<int[]> list) {
        int n = list.size();
        Integer[][] dp = new Integer[n][x + 10];
        return traverse(0, x, list, 0, dp);
    }

    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        List<int[]>[] list = new ArrayList[n];
        for (int i = 0; i < n; i++) {
            list[i] = new ArrayList();
        }

        for (int i = 0; i < queries.length; i++) {
            int l = queries[i][0], r = queries[i][1], x = queries[i][2];
            for (int j = l; j <= r; j++) {
                list[j].add(new int[] { x, i + 1 });
            }
        }

        int mx = 0, valid = 0;
        for (int i = 0; i < n; i++) {
            if(nums[i] == 0){
                valid++;
                continue;
            }
            int x = compute(nums[i], list[i]);
            if (x != Integer.MAX_VALUE){
                mx = Math.max(x, mx); 
                valid++;
            }
        }

        return valid < nums.length ? -1 : mx;
    }
}

