class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        const int n = int(nums.size());
        const int q = int(queries.size());

        bitset<1000 + 5> dp;

        auto check = [&](int middle) -> bool {
            vector<vector<int>> adi(n);
            for(int i = 0; i <= middle; i++) {
                int l = queries[i][0];
                int r = queries[i][1];
                int val = queries[i][2];

                for(int j = l; j <= r; j++) {
                    adi[j].emplace_back(val);
                }
            }

            bool ok = true;
            for(int i = 0; i < n; i++) {
                dp.reset();
                dp[0] = 1;

                if(dp[nums[i]]) {
                    continue;
                }

                bool found = false;
                for(auto& x: adi[i]) {
                    dp |= (dp << x);

                    if(dp[nums[i]]) {
                        found = true;
                        break;
                    }
                }
                ok &= found;
            }

            return ok;
        };

        int left = -2; // always bad
        int right = q; // always good
        while(left + 1 < right) {
            int middle = (left + right) >> 1;

            if(check(middle)) right = middle;
            else left = middle;
        }

        if(right == q) return -1;
        else return right + 1;
    }
};

