class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        if not nums or sum(nums) == 0:
            return 0

        m, n = len(queries), len(nums)

        def can_form(ops, target):
            # print((ops, target))
            dp = [False] * (target + 1)
            dp[0] = True
            for i in range(10):
                op_count = ops[i]
                op = i+1
                for _ in range(op_count):
                    for j in range(target, op-1, -1):
                        dp[j] = dp[j] | dp[j-op]

            # print(dp)
            return dp[target]


        def to_zero(k):
            # print("k==", k)
            # print("n==", n)
            memo = [[0] * 10 for _ in range(n+1)]
            for i in range(k):
                l, r, val = queries[i]
                # print(" queries[i]==",  queries[i])
                memo[l][val-1] += 1
                memo[r+1][val-1] -= 1

            ops = [0] * 10
            for i in range(n):
                x = nums[i]
                for y in range(10):
                    ops[y] += memo[i][y]
                if x == 0:
                    continue
                # print("i==", i)
                if not can_form(ops, x):
                    return False

            return True

        if not to_zero(m):
            return -1

        l, r = 0, m
        while l<r:
            mid = l + (r-l)//2
            if to_zero(mid):
                r = mid
            else:
                l = mid+1

        return r

