class Solution {
public:
    bool check(int mid, vector<int>& nums, vector<vector<int>>& queries){
        for(int i = 0; i < nums.size(); i++){
            vector<int> v;
            for(int j = 0; j < mid && j < queries.size(); j++){
                if(queries[j][0] <= i && queries[j][1] >= i){
                    v.push_back(queries[j][2]);
                }
            }
            vector<bool> dp(nums[i]+1);
            dp[0] = true;
            for(int j = 0; j < v.size(); j++){
                for(int k = nums[i]-v[j]; k >= 0; k--){
                    if(dp[k]){
                        dp[k+v[j]] = true;
                    }
                }
            }
            if(!dp[nums[i]]) return false;
        }
        return true;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int l = 0, r = queries.size();
        while(l <= r){
            int mid = (l+r)/2;
            if(check(mid, nums, queries)){
                r = mid-1;
            }
            else{
                l = mid+1;
            }
        }
        if(l == queries.size()+1){
            return -1;
        }
        return l;
    }
};

