class Solution {
    fun minZeroArray(nums: IntArray, queries: Array<IntArray>): Int {
        var maxK = 0
        for (j in nums.indices) {
            val target = nums[j]
            if (target == 0) {
                continue
            }

            val jQueries = mutableListOf<Pair<Int, Int>>()
            for (i in queries.indices) {
                val query = queries[i]
                val l = query[0]
                val r = query[1]
                val v = query[2]
                if (j in l..r) {
                    jQueries.add(i to v)
                }
            }

            if (jQueries.isEmpty()) {
                return -1
            }

            jQueries.sortBy { it.first }

            val dp = mutableMapOf<Int, Int>()
            dp[0] = -1

            for ((i, v) in jQueries) {
                val newDp = mutableMapOf<Int, Int>()
                for ((sum, currentMax) in dp) {
                    if (sum !in newDp || currentMax < newDp[sum]!!) {
                        newDp[sum] = currentMax
                    }

                    val newSum = sum + v
                    if (newSum > target) {
                        continue
                    }

                    val newMax = maxOf(currentMax, i)
                    if (newSum !in newDp || newMax < newDp.getOrDefault(newSum, Int.MAX_VALUE)) {
                        newDp[newSum] = newMax
                    }
                }
                dp.clear()
                dp.putAll(newDp)
            }

            if (target !in dp) {
                return -1
            }

            val earliestMaxIndex = dp[target]!!
            val earliestKJ = earliestMaxIndex + 1
            maxK = maxOf(maxK, earliestKJ)
        }

        return maxK
    }
}

