class Solution {
    public  boolean isSubsetSum(List<Integer> arr, int sum) {
        int n = arr.size();
        boolean[][] dp = new boolean[n + 1][sum + 1];
        for (int i = 0; i <= n; i++) {
            dp[i][0] = true;
        }
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= sum; j++) {
                if (j < arr.get(i - 1)) {
                    dp[i][j] = dp[i - 1][j];
                }
                else {
                    dp[i][j] = dp[i - 1][j] || dp[i - 1][j - arr.get(i - 1)];
                }
            }
        }
        return dp[n][sum];
    }
    public boolean isZeroArray(int[] nums, int n,int k,int[][] queries) {
        List<Integer>[] arr=new ArrayList[n];
        for(int i=0;i<n;i++) arr[i]=new ArrayList<>();
        for(int i=0;i<=k;i++){
            int[] q=queries[i];
            int x=q[0];
            int y=q[1];
            int z=q[2];
            for(int j=x;j<=Math.min(n-1,y);j++){
                arr[j].add(z);
            }
        }
        int sum=0;
        for(int i=0;i<n;i++){
            int val=nums[i];
            if(!isSubsetSum(arr[i],val)) return false;
        }
        return true;
    }
    public boolean isZero(int[] arr){
        for(int val:arr){
            if(val!=0) return false;
        }
        return true;
    }
    public int minZeroArray(int[] nums, int[][] queries) {
        int n=nums.length;
        if(isZero(nums)) return 0;
        int m=queries.length;
        int ans=-1;
        int low=0;
        int high=m-1;
        while(low<=high){
            int mid=(low+high)/2;
            if(isZeroArray(nums,n,mid,queries)){
                ans=mid+1;
                high=mid-1;
            }else low=mid+1;
        }
        return ans;

    }
}

