class Solution {
public:
    int minZeroArray(vector<int>& a, vector<vector<int>>& q) {
        int n=a.size(),m=q.size();
        int ans=0;
        for(int i=0;i<n;i++){
            int lo=0,hi=m,bsans=m+1;
            while(hi>=lo){
                int mid=lo+(hi-lo)/2;
                vector<int> op;
                for(int j=0;j<mid;j++){
                    op.push_back((q[j][0]<=i && q[j][1]>=i ? q[j][2]:0));
                }
                vector<vector<int>> dp(mid+1,vector<int>(a[i]+1,-1));
                auto func=[&](int j,int val,auto &&func)->bool{
                    if(j==mid)return val==0;
                    if(dp[j][val]!=-1)return dp[j][val];
                    bool ok=false;
                    if(val>=op[j]){
                        ok = func(j+1, val - op[j], func) || func(j+1, val, func);
                    }else{ok=(ok || func(j+1,val,func));}
                    return dp[j][val]=ok;
                };
                if(func(0,a[i],func)){
                    bsans=mid;
                    hi=mid-1;
                }else lo=mid+1;
            }
            ans=max(ans,bsans);
        }
        return (ans==m+1?-1:ans);
    }
};

