class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        l ,r = 0,len(queries)

        def good(x: int, p: List[int]):
            if x == 0:
                return True
            f = [0] * (x + 1)
            f[0] = 1
            z = []
            for y in range(1, 11):
                z += [y] * p[y]
            # print(x, p, z)
            for y in z:
                for i in range(x, -1, -1):
                    if f[i] > 0 and i + y <= x:
                        f[i + y] = 1
                        if f[x] > 0:
                            return True
            return False

        def ok(x):
            d = [[0 for _ in range(11)] for _ in range(len(nums) + 1)]
            for i in range(x):
                a, b, v = queries[i]
                d[a][v] += 1
                d[b+1][v] -= 1
            p = [0] * 11
            for i in range(len(nums)):
                for j in range(11):
                    p[j] += d[i][j]
                if not good(nums[i], p):
                    return False
            return True

        if not ok(r):
            return -1
        if ok(l):
            return 0
        while l + 1 < r:
            mid = (l+r) // 2
            if ok(mid):
                r = mid
            else:
                l = mid
        return r

