            vector<vector<pair<int, int>>> changes(n);
            for (int j = 0; j < queries.size(); j++) {
                auto& query = queries[j];
                for (int i = query[0]; i <= query[1]; ++i) {
                    changes[i].push_back({query[2], j + 1});
                }
            }
            int res = 0;
            bitset<1001> dp;
            for (int i = 0; i < n; i++) {
                dp.reset();
                dp[0] = 1;
                int k = 0, m = changes[i].size();
                while (!dp[nums[i]] && k < m) {
                    res = max(res, changes[i][k].second);
                    dp |= (dp << changes[i][k++].first);
                }
                if (!dp[nums[i]]) {
                    return -1;
                }
            }
            return res;
        }
    };

