class Solution {
public:
    int dp[1010][1010];
    int solve(int j, int num, int i, vector<vector<int>>& queries){
        if(i == queries.size()){
            return 1010;
        }
        if(dp[i][num] != -1) return dp[i][num];

        int ans = solve(j, num, i+1, queries);
        if(j >= queries[i][0] && j <= queries[i][1] && num >= queries[i][2]){
            if(num == queries[i][2]){
                ans = i;
            }else{
                ans = min(ans, solve(j, num-queries[i][2], i+1, queries));
            }
        }

        return dp[i][num] = ans;
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int q = queries.size();

        int cnt = 0;
        for(int i=0; i<n; i++){
            if(nums[i] == 0) cnt++;
        }

        if(cnt == n) return 0;

        vector<int> ans(n, 0);
        for(int j=0; j<n; j++){
            if(nums[j] == 0) continue;
            memset(dp, -1, sizeof(dp));
            ans[j] = solve(j, nums[j], 0, queries);
        }

        int mx = *max_element(ans.begin(), ans.end());
        if(mx == 1010){
            return -1;
        }

        return mx+1;
    }
};

