class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int ans = 0;
        for (int i = 0; i < nums.length; i++) {
            int num = nums[i];
            int min = getMin(queries, num, i);
            if (min == -1) {
                return -1;
            }
            ans = Math.max(ans, min);
        }
        return ans;
    }

    private int getMin(int[][] queries, int num, int idx) {
        if (num == 0) {
            return 0;
        }
        int m = queries.length;
        boolean[][] dp = new boolean[m + 1][num + 1];
        dp[0][0] = true;
        for (int i = 1; i <= m; i++) {
            int l = queries[i - 1][0];
            int r = queries[i - 1][1];
            int v = queries[i - 1][2];
            for (int j = 0; j <= num; j++) {
                dp[i][j] = dp[i - 1][j];
                if (idx < l || idx > r) {
                    continue;
                }
                if (j >= v) {
                    dp[i][j] |= dp[i - 1][j - v];
                }
            }
            if (dp[i][num]) {
                return i;
            }
        }
        return -1;
    }
}

