bool can(int k) {
    // cout << "can " << k << endl;
    int n = arr.size();
    vector<vector<int>> vals(n);

    // need to check time complexity here as well..
    // k <= qn .. q * n ??
    for (int i = 0; i <= k; ++i) {
        int l = q[i][0];
        int r = q[i][1];
        int v = q[i][2];

        for (int j = l; j <= r; ++j) {
            vals[j].push_back(v);
        }
    }


    for (int i = 0; i < n; ++i) {
        if (arr[i] == 0) continue;
        bool ans = subsetSum(vals[i], arr[i]);
        if (not ans) return false;
    }
    // cout << "true for " << k << endl;
