}


class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {

        arr = nums;
        q = queries;

        bool nonZero = false;

        for(int x: arr) if (x) nonZero = true;

        if (not nonZero) return 0;


        int ans = -1;
        int qn = queries.size();
        for (int step = qn; step > 0; step /= 2) {
            while (step + ans < qn and not can(ans + step)) {
                ans += step;
            }
        }

        if (ans == qn - 1) return -1;
        return ans + 2;

    }
};

