class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        q = len(queries)

        ans = 0
        for i in range(n):
            curr = nums[i]
            if curr == 0:
                continue #no action necessary

            earliest = -1
            valqueues = [[0] for i in range(11)]
            seen = [False] * (curr + 1)
            seen[0] = True

            for k in range(q):
                l, r, v = queries[k]
                if i < l or i > r:
                    continue

                old = valqueues[v]
                valqueues[v] = []
                for op in old:
                    newop = op + v
                    if newop > curr or seen[newop]:
                        continue

                    seen[newop] = True
                    if newop == curr:
                        earliest = k + 1
                        break
                    for l in range(1, 11):
                        valqueues[l].append(newop)

            if earliest == -1: return -1 #could not find a combo
            ans = max(ans, earliest)

        return ans

