class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int q = queries.size(), n = nums.size(), MX = 1000;
        int dp[1001][10][1001];
        // dp[q + 1][n][MX + 1];
        for (int i = 0; i <= q; i++){
            for (int j = 0; j < n; j++){
                for (int k = 0; k <= MX; k++) dp[i][j][k] = 0;
            }
        }
        for (int i = 0; i < n; i++) dp[0][i][nums[i]] = 1;

        for (int i = 1; i <= q; i++){
            int valid = 1;
            for (int j = 0; j < n; j++) valid &= dp[i - 1][j][0];
            if (valid) return i - 1;

            int l = queries[i - 1][0], r = queries[i - 1][1], val = queries[i - 1][2];
            for (int j = 0; j < n; j++){
                for (int k = 0; k <= MX; k++) dp[i][j][k] |= dp[i - 1][j][k];
            }
            for (int j = l; j <= r; j++){
                for (int k = val; k <= MX; k++){
                    dp[i][j][k - val] |= dp[i][j][k];
                }
            }
            if (i == q){
                valid = 1;
                for (int j = 0; j < n; j++) valid &= dp[i][j][0];
                if (valid) return i;
            }
        }
        return -1;
    }
};

