    inline bool starts_with(const string &s, const string &prefix) { return s.rfind(prefix, 0) == 0; }
    inline bool ends_with(const string &s, const string &suffix) { return s.size() >= suffix.size() && s.compare(s.size() - suffix.size(), suffix.size(), suffix) == 0; }
    inline bool is_numeric(const string &s) { return !s.empty() && all_of(s.begin(), s.end(), ::isdigit); }
    vector<string> split(const string &s, const string &delimiter = " ") {
        vector<string> tokens; size_t start = 0, end;
        while ((end = s.find(delimiter, start)) != string::npos) { tokens.push_back(s.substr(start, end - start)); start = end + delimiter.length(); }
        tokens.push_back(s.substr(start));
        return tokens;
    }
    string join(const vector<string> &tokens, const string &delimiter = " ") {string result; for (size_t i = 0; i < tokens.size(); i++) { result += tokens[i]; if (i < tokens.size() - 1) result += delimiter; } return result;}
    string strip(const string &s) {
        size_t start = s.find_first_not_of(" tnrfv");
        if (start == string::npos) return "";
        size_t end = s.find_last_not_of(" tnrfv");
        return s.substr(start, end - start + 1);
    }
    string lstrip(const string &s, char ch) {size_t start = 0; while (start < s.length() && s[start] == ch) ++start; return s.substr(start);}
    string rstrip(const string &s, char ch) {size_t end = s.length(); while (end > 0 && s[end - 1] == ch) --end; return s.substr(0, end);}
    size_t count(const string &s, const string &substring) {
        size_t count = 0, pos = 0;
        while ((pos = s.find(substring, pos)) != string::npos) { ++count; pos += substring.length(); }
        return count;
    }
    string replace_all(const string &s, const string &from, const string &to) {
        string result; size_t pos = 0, start = 0;
        while ((pos = s.find(from, start)) != string::npos) { result += s.substr(start, pos - start) + to; start = pos + from.length(); }
        result += s.substr(start);
        return result;
    }
