    template <typename T> bool isPalindrome(const std::vector<T>& vec) {
        size_t left = 0, right = vec.size() - 1;
        while (left < right) {
            if (vec[left] != vec[right]) {
                return false;
            }
            ++left;
            --right;
        }
        return true;
    }
