    template <typename T> void countingSort(vector<T> &arr) {
        if (arr.empty()) return;
        T minVal = *min_element(arr.begin(), arr.end());
        T maxVal = *max_element(arr.begin(), arr.end());
        T range = maxVal - minVal + 1;
        vector<T> count(range, 0);
        for (T num : arr) {
            count[num - minVal]++;
        }
        int index = 0;
        for (T i = 0; i < range; ++i) {
            while (count[i] > 0) {
                arr[index++] = i + minVal;
                count[i]--;
            }
        }
    }
    // Next Greater Element (Strict)
    template <typename T>
    std::vector<int> nge_strict(const std::vector<T>& arr) {
        size_t n = arr.size();
        std::vector<int> nge(n, n);  // Default to n if no greater element is found
        std::stack<int> st;
        for (size_t i = n; i-- > 0;) {  // Iterate from the back
            while (!st.empty() && arr[st.top()] <= arr[i]) st.pop();  // Pop elements that are less than or equal to current
            if (!st.empty()) nge[i] = st.top();  // Assign the index of the next greater element
            st.push(i);  // Push the current index onto the stack
        }
        return nge;
    }
    // Next Greater Element (Loose)
    template <typename T>
    std::vector<int> nge_loose(const std::vector<T>& arr) {
        size_t n = arr.size();
        std::vector<int> nge(n, n);  // Default to n if no greater/equal element is found
        std::stack<int> st;
        for (size_t i = n; i-- > 0;) {  // Iterate from the back
            while (!st.empty() && arr[st.top()] < arr[i]) st.pop();  // Pop elements that are less than the current
            if (!st.empty()) nge[i] = st.top();  // Assign the index of the next greater element
            st.push(i);  // Push the current index onto the stack
        }
        return nge;
    }
