    class MEXSet {
    private:
        map<int, int> freq; 
        set<int> missing;   
    public:
        MEXSet() {missing.insert(0);}
        void add(int x) {
            freq[x]++;
            missing.erase(x); 
            if (!freq.count(x + 1)) missing.insert(x + 1);
        }
        void remove(int x) {
            if (freq[x] > 0) {
                freq[x]--;
                if (freq[x] == 0) {
                    freq.erase(x);
                    missing.insert(x);
                }
            }
        }
        void removeAll(int x) {
            if (freq.count(x)) { 
                freq.erase(x); 
                missing.insert(x); 
            }
        }
        int getMEX() {return *missing.begin(); }
    };
    adjList<ll> make_adjList(int n, vector<vector<int>>&edges, bool oneBased = false, bool directed = false)
    {
        int m = edges.size();
        adjList<ll> adj(n);
        for(int i=0;i<m;i++)
        {
            int u = edges[i][0], v = edges[i][1];
            u -= oneBased, v -= oneBased;
            adj[u].push_back(v);
            if(not directed) adj[v].push_back(u);
        }
        return adj;
    }
    w_adjList<ll> make_w_adjList(int n,vector<vector<int>>&edges,bool oneBased = false, bool directed = false)
    {
        int m = edges.size();
        w_adjList<ll> adj(n);
        for(int i=0;i<m;i++)
        {
            int u = edges[i][0], v = edges[i][1], w = edges[i][2];
            u -= oneBased, v -= oneBased;
            adj[u].push_back({v,w});
            if(not directed) adj[v].push_back({u,w});
        }
        return adj;
    }
    template<typename T> vector<int> topoSort(vector<T>&adj, bool weighted=false)
    {
        int n = adj.size();
        vector<int> res;
        vector<bool> vis(n,false);
        auto dfs = [&](auto &&self, int i)->void{
            vis[i] = true;
            for(auto &it:adj[i])
            {
                int adjNode = weighted ? it[0] : it;
                if(!vis[adjNode]) self(self,adjNode);
            }
            res.push_back(i);
        };
        for(int i=0;i<n;i++)
        {
            if(vis[i]) continue;
            dfs(dfs,i);
        }
        reverse(res.begin(),res.end());
        return res;
    }

