    // ======================== B I T  M A N I P U L A T I O N  ======================================
    inline int popcount(ll x) { return __builtin_popcountll(x); }
    inline int lowbit(ll x) { return (x == 0 ? -1 : __builtin_ctzll(x)); }
    inline bool isPowerOfTwo(ll x) { return x && (!(x & (x - 1))); }
    inline int highbit(ll x) { return x == 0 ? -1 : (63 - __builtin_clzll(x)); }
    // Get all bits between lowest and highest set bits inclusive
    inline ll bitMask(ll x) { if (x == 0) return 0;return (1LL << (highbit(x) + 1)) - (1LL << lowbit(x));}
    // Get length of number in bits (length of binary representation)
    inline bool isSubMask(ll n, ll check) { return (n | check) == n; }
    inline int bitLength(ll x) { return x ? highbit(x) + 1 : 0; }
    // Check if all bits in a number are contiguous
    inline bool isContiguous(ll x) { return (x & bitMask(x)) == bitMask(x); }
    // Count the number of set bits in a range
    inline int countBitsInRange(ll x, int left, int right) {return __builtin_popcountll((x >> left) & ((1LL << (right - left + 1)) - 1));}
    // Get the next higher number with the same number of 1-bits
    inline ll nextHigherSameOnes(ll n) {
        ll c = n & -n;
        ll r = n + c;
        return (((n ^ r) / c) >> 2) | r;
    }
    // Get the previous smaller number with the same number of 1-bits
    inline ll prevLowerSameOnes(ll n) {
        if((n&(n+1)) == 0) return n;
        if(!(n&1) and ((n>>1)&1)) return n-1;
        ll k = 1, temp = n;
        while(temp)
        {
            if((n&(1<<k)) and !(n&(1<<(k-1))))
                break;
            k++;
            temp = temp>>1;
        }
        ll p = 1LL<<(k-1);
        ll c = __builtin_popcount(n&(p-1));
        ll x = n-p;
        return (x&(~(p-1)))|(p - (1LL<<(k-1-c)));
    }
    inline ll setBit(ll x, int i) {
        return x | (1LL << i);
    }
    inline ll clearBit(ll x, int i) {
        return x & ~(1LL << i);
    }
    inline ll toggleBit(ll x, int i) {
        return x ^ (1LL << i);
    }
    inline bool getBit(ll x, int i) {
        return (x >> i) & 1;
    }
    inline int lowUnsetBit(ll x) {
        return __builtin_ctzll(~x);
    }
    // Reverse the bits of a number
    inline ll reverseBits(ll x) {
        ll res = 0;
        while (x > 0) {
            res = (res << 1) | (x & 1);
            x >>= 1;
        }
        return res;
    }
    inline ll negateBits(ll x) {
        // Find the bit length of the number
        int len = bitLength(x);
        // Create a mask with 'len' bits set to 1
        ll mask = (1LL << len) - 1;  // This gives a mask with all bits up to MSB set to 1
        // XOR x with the mask to flip the bits
        return x ^ mask;
    }
    inline ll xorTill(ll n){
        if(n % 4 == 1) return 1; 
        else if(n % 4 == 2) return n + 1; 
        else if(n % 4 == 3) return 0; 
        else return n; 
    }
    inline ll rangeXor(ll L, ll R) {
        ll xorTillL = xorTill(L - 1);
        ll xorTillR = xorTill(R);
        return xorTillL ^ xorTillR;
    }
    vector<ll> setBitCount(ll n) {
        vector<ll> res(64, 0);
        if(n <= 0) return res;
        ll p0 = 1, p1 = 2;
        ll i = 0;
        while(i<63) {
            res[i] = ((n + 1) / p1) * p0 + max(0LL, (n + 1) % p1 - p0);
            p0 = p1, p1 *= 2;
            i++;
        }
        return res;
    }
    ll rangeAnd(ll L, ll R) {
        vector<ll> tillL = setBitCount(L - 1);
        vector<ll> tillR = setBitCount(R);
        ll ans = 0;
        for(ll i = 0; i < 64; i++) if(tillR[i] - tillL[i] == (R - L + 1)) ans |= (1LL << i);
        return ans;
    }
    ll rangeOr(ll L, ll R) {
        vector<ll> tillL = setBitCount(L - 1);
        vector<ll> tillR = setBitCount(R);
        ll ans = 0;
        for(ll i = 0; i < 64; i++) if(tillR[i] - tillL[i]) ans |= (1LL << i);
        return ans;
    }

