    /*
        does not handle the case for two same points, that is 
        for x1=x2 and y1=y2.
    */
    struct slope{
        int sign;
        ll n,d;
        slope(ll x1,ll y1, ll x2, ll y2)
        {
            ll n = abs(y2-y1), d = abs(x2-x1), g = gcd(n,d);
            auto signFinder = [](ll a, ll b){
                if(!a or !b) return 1;
                else if(a<0 and b<0) return 1;
                else if(a>0 and b>0) return 1;
                return -1;
            };
            int sign = signFinder(y2-y1,x2-x1);
            this->sign = sign;
            this->n = d==0 ? 1 : n/g;
            this->d = n==0 ? 1 : d/g;
        }
        bool operator==(slope&other) 
        { return (sign==other.sign and n==other.n and d==other.d); }
    };
