        // Manhattan Distance (sum of absolute differences of x and y)
        inline long long manhattan(Point& a) const { return abs(a.x - x) + abs(a.y - y); }
        // Squared Euclidean Distance (avoids floating-point precision issues)
        inline long long euclid_sqr(Point& a) const { return (a.x - x) * (a.x - x) + (a.y - y) * (a.y - y); }
        // Overloading the '+' operator to add two points
        inline Point operator+(const Point& p) const { return Point(x + p.x, y + p.y); }
        // Overloading the '-' operator to subtract two points
        inline Point operator-(const Point& p) const { return Point(x - p.x, y - p.y); }
        // Overloading the '*' operator for scalar multiplication
        inline Point operator*(long long scalar) const { return Point(x * scalar, y * scalar); }
        // Overloading the '/' operator for scalar division
        inline Point operator/(long long scalar) const { return Point(x / scalar, y / scalar); }
        // Dot product of two points (treated as vectors)
        inline long long dot(const Point& p) const { return x * p.x + y * p.y; }
        // Cross product of two points (2D version)
        inline long long cross(const Point& p) const { return x * p.y - y * p.x; }
        // Equality comparison (==)
        inline bool operator==(const Point& p) const { return x == p.x && y == p.y; }
        // Inequality comparison (!=)
        inline bool operator!=(const Point& p) const { return !(*this == p); }
        // Print function for easy debugging and visualization
        inline void debugPoint() const { cout << "(" << x << ", " << y << ")" << endl; }
         // Pre-increment (increments x and y before returning the updated Point)
        inline Point& operator++() { ++x; ++y; return *this;}
        // Post-increment (returns current Point, then increments x and y)
        inline Point operator++(int) { Point temp = *this; ++x; ++y; return temp;  }
        // Pre-decrement (decrements x and y before returning the updated Point)
        inline Point& operator--() { --x; --y; return *this; }
        // Post-decrement (returns current Point, then decrements x and y)
        inline Point operator--(int) { Point temp = *this; --x; --y; return temp; }
