    //============================  M O D U L A R  I N T   ==============================================  
       struct mi {
        long long v;
        static const int MOD = 1e9 + 7; 
        explicit operator int() const { return v; }
        mi() : v(0) {}
        mi(long long _v) : v(_v % MOD) { if (v < 0) v += MOD; }
        mi& operator+=(mi b) { if ((v += b.v) >= MOD) v -= MOD; return *this; }
        mi& operator-=(mi b) { if ((v -= b.v) < 0) v += MOD; return *this; }
        mi& operator*=(mi b) { v = (v * b.v) % MOD; return *this; }
        mi& operator/=(mi b) { return *this *= b.inv(); }
        mi operator-() const { return mi(-v); }
        mi pow(long long p) const { 
            mi res = 1, a = *this; 
            while (p) { 
                if (p & 1) res *= a; 
                a *= a,p /= 2; 
            } 
            return res; 
        }
        mi inv() const { return pow(MOD - 2); }
        mi operator+(mi b) const { mi temp = *this; return temp += b; }
        mi operator-(mi b) const { mi temp = *this; return temp -= b; }
        mi operator*(mi b) const { mi temp = *this; return temp *= b; }
        mi operator/(mi b) const { mi temp = *this; return temp /= b; }
        bool operator!=(const mi& b) const { return v != b.v; }
        bool operator==(const mi& b) const { return v == b.v; }
        bool operator>(const mi& b) const { return v > b.v; }
        bool operator<(const mi& b) const { return v < b.v; }
        bool operator>=(const mi& b) const { return v >= b.v; }
        bool operator<=(const mi& b) const { return v <= b.v; }
        mi& operator+=(long long b) { return *this += mi(b); }
        mi& operator-=(long long b) { return *this -= mi(b); }
        mi& operator*=(long long b) { return *this *= mi(b); }
        mi& operator/=(long long b) { return *this /= mi(b); }
        mi operator+(long long b) const { return *this + mi(b); }
        mi operator-(long long b) const { return *this - mi(b); }
        mi operator*(long long b) const { return *this * mi(b); }
        mi operator/(long long b) const { return *this / mi(b); }
        bool operator==(long long b) const { return v == mi(b).v; }
        bool operator!=(long long b) const { return v != mi(b).v; }
        bool operator>(long long b) const { return v > mi(b).v; }
        bool operator<(long long b) const { return v < mi(b).v; }
        bool operator>=(long long b) const { return v >= mi(b).v; }
        bool operator<=(long long b) const { return v <= mi(b).v; }
    };
