    inline ll safe_mod(ll a, ll m = MOD) { return ((a % m) + m) % m;}
    inline ll mod_add(ll a, ll b, ll m = MOD) { return (a % m + b % m) % m; }
    inline ll mod_sub(ll a, ll b, ll m = MOD) { return ((a % m - b % m) + m) % m; }
    inline ll mod_mul(ll a, ll b, ll m = MOD) { return (a % m * b % m) % m; }
    inline long long normalize(long long x, long long mod=MOD) { x %= mod; if (x < 0) x += mod; return x; }
    ll mod_exp(ll base, ll exp, ll m = MOD) {
        ll result = 1;
        base %= m;
        while (exp > 0) {
            if (exp & 1) result = (result * base) % m;
            base = (base * base) % m;
            exp >>= 1;
        }
        return result;
    }
    inline ll mod_inv(ll a, ll m = MOD) { return mod_exp(a, m - 2, m); }
    inline ll mod_div(ll a, ll b, ll m = MOD) { return mod_mul(a, mod_inv(b, m), m); }

