    // when not precomputed : 
    long long comb(int n, int r) {
        if (r > n || r < 0) return 0;
        long long num = 1, denom = 1;
        for (int i = 0; i < r; ++i) {
            num = (num * (n - i)) % MOD;   
            denom = (denom * (i + 1)) % MOD; 
        }
        return (num * mod_inv(denom, MOD)) % MOD; 
    }
    long long perm(int n, int r) {
        if (r > n || r < 0) return 0;
        long long result = 1;
        for (int i = 0; i < r; ++i) {
            result = (result * (n - i)) % MOD;
        }
        return result;
    }
    // precomputations:
    vector<ll> fact, inv_fact; 
    void init_factorials(ll m = MOD) {
        fact = vector<ll>(N+1, 1);
        inv_fact = vector<ll>(N+1, 1);
        for (int i = 2; i <= N; i++) fact[i] = mod_mul(fact[i - 1], i, m);
        inv_fact[N] = mod_inv(fact[N], m);
        for (int i = N - 1; i >= 0; i--) inv_fact[i] = mod_mul(inv_fact[i + 1], i + 1, m);
    }
    vector<ll> logarithm;
    void precompute_logarithm(int n)
    {
        logarithm = vector<ll>(n+1,0);
        for(int i=2;i<=n;i++) logarithm[i] = logarithm[i/2] + 1;
    }
    // when precomputed : 
    inline ll nCr(ll n, ll r, ll m = MOD) {
        if (r > n || r < 0) return 0;
        return mod_mul(fact[n], mod_mul(inv_fact[r], inv_fact[n - r], m), m);
    }
    inline ll nPr(ll n, ll r, ll m = MOD) {
        if (r > n || r < 0) return 0;
        return mod_mul(fact[n], inv_fact[n - r], m);
    }
    // Lucas' theorem for calculating nCr mod p, where p is a small prime number (<=4000)
    struct Lucas {
        int p;
        vector<vector<int>> pascal;
        Lucas(int prime) : p(prime) {
            pascal.assign(p, vector<int>());
            for (int i = 0; i < p; i++) {
                pascal[i].assign(i + 1, 0);
                pascal[i][0] = 1;
                pascal[i][i] = 1;
                for (int j = 1; j < i; j++)
                    pascal[i][j] = (pascal[i - 1][j - 1] + pascal[i - 1][j]) % p;
            }
        }
        int nCr(long long n, long long r) {
            if (r > n)
                return 0;
            int res = 1;
            while (n || r) {
                int n_digit = n % p;
                int r_digit = r % p;
                if (r_digit > n_digit)
                    return 0;
                res = (int)((1LL * res * pascal[n_digit][r_digit]) % p);
                n /= p;
                r /= p;
            }
            return res;
        }
    };
    bool is_prime1(ll n) { 
        if (n <= 1) return false; 
        if (n <= 3) return true; 
        if (n % 2 == 0 || n % 3 == 0) return false; 
        for (ll i = 5; i * i <= n; i += 6) 
            if (n % i == 0 || n % (i + 2) == 0) return false; 
        return true; 
    }
    bool is_prime2(ll n) {
        if (n <= 1) return false;
        if (n <= 3) return true;
        if (n % 2 == 0 || n % 3 == 0) return false;

        ll d = n - 1;
        ll r = 0;
        while (d % 2 == 0) {
            d /= 2;
            r++;
        }

        const ll K = 6; // Number of iterations (witnesses)
        for (ll i = 0; i < K; i++) {
            ll a = 2 + rand() % (n - 4); // Random number in [2, n-2]
            ll x = mod_exp(a, d, n);
            if (x == 1 || x == n - 1) continue;

            bool composite = true;
            for (ll j = 0; j < r - 1; j++) {
                x = mod_mul(x, x, n);
                if (x == n - 1) {
                    composite = false;
                    break;
                }
            }
            if (composite) return false;
        }
        return true;
    }
    vector<ll> maxPrimeFactor;
    void precompute_max_prime_factor(ll n) {
        maxPrimeFactor = vector<ll>(n + 1, 0);
        for (int i = 2; i <= n; i++) {
            if (maxPrimeFactor[i] == 0) { // i is prime
                for (int j = i; j <= n; j += i) {
                    maxPrimeFactor[j] = i; // Update to latest prime factor (i)
                }
            }
        }
    }
     vector<ll> minFactor;
     void precompute_min_prime_factor( ll n )
     {
        minFactor = vector<ll>(n + 1, 0);
        for (int i = 2; i <= n; i++) minFactor[i] = i; 
        for (int i = 2; i * i <= n; i++) {
            if (minFactor[i] == i) { // i is a prime
                for (int j = i * i; j <= n; j += i) {
                    minFactor[j] = min(minFactor[j], (ll)i); // Update with smallest prime factor
                }
            }
        }
     }
    vector<pair<ll,ll>> fastPrimeFactorize(ll n)
    {
        vector<pair<ll,ll>> result;
        while(n!=1)
        {
            if(result.empty() || result.back().first != minFactor[n]) result.push_back({minFactor[n], 1});
            else result.back().second++;
            n /= minFactor[n];
        }
        return result;
    }
    // Get prime factorization of a number
    vector<pair<ll,ll>> primeFactorize(ll n) {
        vector<pair<ll,ll>> factors;
        for(ll i = 2; i * i <= n; i++) {
            if(n % i == 0) {
                ll cnt = 0;
                while(n % i == 0) {
                    cnt++;
                    n /= i;
                }
                factors.push_back({i, cnt});
            }
        }
        if(n > 1) factors.push_back({n, 1});
        return factors;
    }
    long long sum_of_divisors(long long n) {
        long long sum = 0;
        for (long long i = 1; i * i <= n; i++) {
            if (n % i == 0) {
                sum += i; 
                if (i != n / i) {
                    sum += n / i; 
                }
            }
        }
        return sum;
    }
    // Euler totient function:
    vector<ll> phi;
    void compute_totient(ll SIEVE_MAX) {
        phi = vector<ll>(SIEVE_MAX);
        for (ll i = 0; i < SIEVE_MAX; i++) phi[i] = i;
        for (ll i = 2; i < SIEVE_MAX; i++) {
            if (phi[i] == i) { // i is a prime
                for (ll j = i; j < SIEVE_MAX; j += i) {
                    phi[j] *= (i - 1);
                    phi[j] /= i;
                }
            }
        }
    }
    struct GCD_type { long long x, y, d; };
    // extended euclidean algorithm( to find solutions to ax + by = gcd(a,b) )
    GCD_type ex_GCD(long long a, long long b)
    {
        if (b == 0) return {1, 0, a};
        GCD_type pom = ex_GCD(b, a % b);
        return {pom.y, pom.x - a / b * pom.y, pom.d};
    }

    // linear diophantine equation (ax + by = c):
    pair<ll,ll> linear_diophantine(ll a, ll b, ll c)
    {
        auto solution = ex_GCD(a,b);
        ll x0 = solution.x, y0 = solution.y, g = solution.d;
        if( c % g ) return {-1,-1};
        return { x0*(c/g), y0*(c/g)};
    } 

    inline ll general_mod_inverse(ll a, ll M)
    {
        auto solution = ex_GCD(a,M);
        if(solution.d != 1) return LLONG_MAX;
        return ((solution.x % M) + M) % M;;
    }
    // chinese remainder theorem
    pair<ll ,ll> crt(vector<pair<ll,ll>>&c)
    {
        ll t = c.size();
        for(ll i = 0; i < t; i++) normalize(c[i].ff , c[i].ss);
        long long ans = c[0].ff;
        long long LCM = c[0].ss;
        for(ll i = 1; i < t; i++)
        {
            // E X P L A N A T I O N:
            /*  
                let our merged congruence for the first i congruences be :
                X  ans mod LCM 
                X + LCM.Z1 = ans -> 1
                current congruence (ith congruence):
                X  a mod m 
                X + m.Z2 = a -> 2
                Substracting 1st equation from second : 
                Z2.m - Z1.LCM = a-ans
                (-Z1).LCM + Z2.m = a-ans -> 3
                let bezous coefficients for this equation be = (X1,Y1)
                so , -Z1 = X1.((a-ans)/d), here d is the gcd(Z1,Z2)
                so , Z1 = -X1.((a-ans)/d), here d is the gcd(Z1,Z2)
                Putting the value of Z1 in 1st equation we get : 
                X = ans + (X1.((a-ans)/d))*LCM

                So, our new congruence becomes: 
                X  ( ans + (X1.((a-ans)/d))*LCM ) % ((m/d)*LCM) -> 4

                Lets prove a nice property of the modulo operator : 
                    ca mod cb = (c.(a mod b)) mod cb
                PROOF : 
                    Let r = a mod b. By definition, this means there exists an integer q 
                    such that a = bq + r  with  0  r < b.
                    Multiplying the entire equation by c gives:
                        ca = c(bq + r) = (cb)q + cr.
                    When dividing ca by cb, the term (cb)q is exactly divisible by cb, 
                    leaving a remainder of cr. Since 0  r < b  implies  0  cr < cb,
                    cr is the unique remainder when ca is divided by cb. In other words,
                    ca mod (cb) = cr = c(a mod b).
                    Thus, we have proved that
                        ca mod (cb) = (c(a mod b)) mod (cb).

                Using this property in equation in equation 4:
                X = (ans + LCM . ( (X1.((a-ans)/d)) % (m/d) ) ) % ((m/d)*LCM)
                Thus we finally get our new congruence my merging the first i+1 congruences as:
                X  ( ans + LCM . ( (X1.((a-ans)/d)) % (m/d) ) ) mod ((m/d)*LCM)
            */
            auto pom = ex_GCD(LCM, c[i].ss);
            ll x1 = pom.x;
            ll d = pom.d;
            if((c[i].ff - ans) % d != 0) return {LLONG_MAX,LLONG_MAX};
            ans = normalize(ans + x1 * (c[i].ff - ans) / d % (c[i].ss / d) * LCM, LCM * c[i].ss / d);
            LCM = lcm(LCM, c[i].ss); // you can save time by replacing above lcm * c[i].s /d by lcm = lcm * c[i].s / d
        }
        return {ans,LCM};
    }

