    vector<ll> divisors(ll n) {
        vector<ll> divs;
        for (ll i = 1; i * i <= n; i++) {
            if (n % i == 0) {
                divs.push_back(i);
                if (i != n / i) divs.push_back(n / i);
            }
        }
        sort(divs.begin(), divs.end());
        return divs;
    }
    // ncr for all 0<=r<=n
    // nCr (Combination): Used when order does not matter.
    vector<long long> nCr_values;
    vector<long long> compute_nCr(ll n) {
       nCr_values = vector<long long>(n + 1, 1);  // nC0 is always 1
        long long current_nCr = 1;  // Initialize with nC0 = 1
        for (ll r = 1; r <= n; r++) {
            current_nCr = current_nCr * (n - r + 1) % MOD;
            // instead of modInverse(r, MOD), we can also use precomputed modinverse values
            current_nCr = current_nCr * mod_inv(r, MOD) % MOD;
            nCr_values[r] = current_nCr;
        }
        return nCr_values;
    }
    // npr for 0<=r<=n
    // nPr (Permutation): Used when order matters.
    vector<long long> nPr_values;
    vector<long long> compute_nPr(ll n) {
        nPr_values = vector<long long>(n + 1, 1);  // nP0 is always 1
        long long current_nPr = 1;  // Initialize with nP0 = 1
        for (ll r = 1; r <= n; r++) {
            // Calculate nPr based on nPr = nPr-1 * (n - r + 1)
            current_nPr = current_nPr * (n - r + 1) % MOD;
            nPr_values[r] = current_nPr;
        }
        return nPr_values;
    }

    inline ll rangeSum(ll a, ll b) { 
        ll x = a, y = b;
        ll ans = ((y * (y + 1)) / 2)  - ((x * (x - 1)) / 2);
        return ans;
    }

    ll power(ll a, ll b)
    {
        ll res = 1;
        while (b > 0) {
            ll bit = (b & 1);
            if (bit) res = res * a;
            a = a * a;
            b = b >> 1;
        }
        return res;
    }
    // logb(x)
    ll lg(ll x,ll b=2){
        if(b==1) return -1; //Modify this as per need
        if(b==2) return 63-__builtin_clzll(x); //O(1) for log2
        ll p=1,ans=0;
        while(true){
            if(p>x) return ans-1;
            if(p>LLONG_MAX/b) return ans;
            p*=b,ans++;
        }
    }

    // n-th root: 
    ll mult(ll a,ll b){
        if(b==0) return 0;
        return a>inf/b?inf:a*b;
    }
    ll binpow(ll a,ll b){
        ll res=1;
        while(b){
            if(b&1) res=mult(res,a);
            a=mult(a,a),b>>=1; 
        }
        return res;
    }
    ll nth_root(ll x,ll n){
        if(x==1||n==1) return x;
        if(x==inf) x--;    // Doesn't make a difference in answer, and check function fails for INF 
        ll l=0,r=x,m;
        while(r-l>1){
            m=l+(r-l)/2;
            if(binpow(m,n)>x) r=m;
            else l=m;
        }
        return l;
    }

    // higest power of x which is a factor of n:
    ll maxDivisiblePower(ll x, ll n)
    {
        if(x==1) return inf;
        ll res = 0;
        while(not (n%x)) n/=x,res++;
        return res;
    } 

