    //1) unordered : 
    template <typename T>
    struct udh {
        // Hash parameters
        ll K1 = 37987, H1 = 79, M1 = 999999937;
        ll K2 = 53887, H2 = 67, M2 = 370881107;
        vector<ll> prefixh1, prefixh2;
        ll n;
        ll getCodeForString(char c) { return c - 'a' + 1; }
        ll getCodeForVector(typename T::value_type x) { return x + 1; }
        udh(const T &container) {
            n = container.size();
            prefixh1.resize(n + 1, 0);
            prefixh2.resize(n + 1, 0);
            for (ll i = 0; i < n; i++) {
                ll code = is_same<T, string>::value ? 
                        getCodeForString(container[i]) : 
                        getCodeForVector(container[i]);
                prefixh1[i + 1] = (prefixh1[i] + mod_exp(code + K1, H1, M1)) % M1;
                prefixh2[i + 1] = (prefixh2[i] + mod_exp(code + K2, H2, M2)) % M2;
            }
        }
        pair<ll, ll> subHash(ll l, ll r) {
            return {
                (prefixh1[r + 1] - prefixh1[l] + M1) % M1,
                (prefixh2[r + 1] - prefixh2[l] + M2) % M2
            };
        }
    };   
    //2) ordered : 
    // common for both:
    struct powers{
        vector<ul> p;
        ul P, M;
        powers(ll n,ul P, ul M)
        {
            this->P = P, this->M = M;
            p.resize(n+1,1);
            for(ll i=1;i<=n;i++) p[i] = (p[i-1]*P)%M;
        }
        ul get(ll i) { return p[i]; }
    };
    // a) for strings : 
    struct SingleHash {
        std::vector<ul> hash;
        powers*p = NULL;
        SingleHash(string&s,powers*p)
        { 
            this->p = p;
            buildHash(s);
        }
        void buildHash(const std::string& s) {
            hash.resize(s.size() + 1, 0);
            for(ll i = s.size()-1; i>=0; i--)
            {
                hash[i] = (hash[i+1]*(p->P) + (s[i]-'a'+1))%(p->M);
            }
        }
        ul subHash(ll l, ll r) {
            if(r<l) return 0;
            ul result = (hash[l] - (hash[r+1]*(p->get(r-l+1)))%(p->M) + (p->M))%(p->M);
            return result;
        }
    };
    struct DoubleHash {
        SingleHash *hash1, *hash2;
        DoubleHash(powers*p1, powers*p2, string&s)
        {
            hash1 = new SingleHash(s,p1);
            hash2 = new SingleHash(s,p2);
        }
        std::vector<ul> subHash(ll l, ll r) 
        { return { hash1->subHash(l,r), hash2->subHash(l,r) }; }
    };
    struct TripleHash {
        SingleHash *hash1, *hash2, *hash3;
        TripleHash(powers*p1, powers*p2, powers*p3, string&s)
        {
            hash1 = new SingleHash(s,p1);
            hash2 = new SingleHash(s,p2);
            hash3 = new SingleHash(s,p3);
        }
        std::vector<ul> subHash(ll l, ll r) 
        {
            return { hash1->subHash(l,r), hash2->subHash(l,r), hash3->subHash(l,r) };
        }
    };
    //b) for vectors:
    struct SingleHashv {
        std::vector<ul> hash;
        powers* p = NULL;
        template <typename T>
        SingleHashv(std::vector<T>& v, powers* p) {
            this->p = p;
            buildHash(v);
        }
        template <typename T> void buildHash(const std::vector<T>& v) {
            hash.resize(v.size() + 1, 0);
            for (ll i = v.size() - 1; i >= 0; i--) {
                hash[i] = (hash[i + 1] * (p->P) + (v[i] + 1)) % (p->M);
            }
        }
        ul subHash(ll l, ll r) {
            if (r < l) return 0;
            ul result = (hash[l] - (hash[r + 1] * (p->get(r - l + 1))) % (p->M) + (p->M)) % (p->M);
            return result;
        }
    };
    struct DoubleHashv {
        SingleHashv* hash1, * hash2;
        template <typename T>
        DoubleHashv(powers* p1, powers* p2, std::vector<T>& v) {
            hash1 = new SingleHashv(v, p1);
            hash2 = new SingleHashv(v, p2);
        }
        std::vector<ul> subHash(ll l, ll r) {
            return { hash1->subHash(l, r), hash2->subHash(l, r) };
        }
    };
    struct TripleHashv {
        SingleHashv* hash1, * hash2, * hash3;
        template <typename T>
        TripleHashv(powers* p1, powers* p2, powers* p3, std::vector<T>& v) {
            hash1 = new SingleHashv(v, p1);
            hash2 = new SingleHashv(v, p2);
            hash3 = new SingleHashv(v, p3);
        }
        std::vector<ul> subHash(ll l, ll r) {
            return { hash1->subHash(l, r), hash2->subHash(l, r), hash3->subHash(l, r) };
        }
    };
    // common for both : 
    template <typename HashType>
    bool sameHash( HashType& a, HashType& b, ll l1, ll r1, ll l2, ll r2) {
        if (r1 < l1 || r2 < l2) return false;
        if ((r1 - l1) != (r2 - l2)) return false;
        auto aHash = a.subHash(l1, r1);
        auto bHash = b.subHash(l2, r2);
        return aHash == bHash;
    }

    // 3) murmur hash : 
    uint32_t murmurhash(const std::string& key, uint32_t seed = 23456789) {  // Default seed set to a more random non-sequential 8-digit prime
        uint32_t len = key.length();
        const uint8_t* data = reinterpret_cast<const uint8_t*>(key.c_str());
        const int nblocks = len / 4;
        uint32_t h1 = seed;
        const uint32_t c1 = 0xcc9e2d51;
        const uint32_t c2 = 0x1b873593;
        const uint32_t* blocks = reinterpret_cast<const uint32_t*>(data);
        for (int i = 0; i < nblocks; i++) {
            uint32_t k1 = blocks[i];
            k1 *= c1;
            k1 = (k1 << 15) | (k1 >> (32 - 15));  // Left rotate by 15 bits
            k1 *= c2;
            h1 ^= k1;
            h1 = (h1 << 13) | (h1 >> (32 - 13));  // Left rotate by 13 bits
            h1 = h1 * 5 + 0xe6546b64;
        }
        const uint8_t* tail = reinterpret_cast<const uint8_t*>(data + nblocks * 4);
        uint32_t k1 = 0;
        switch (len & 3) {
            case 3: k1 ^= tail[2] << 16;
            case 2: k1 ^= tail[1] << 8;
            case 1: k1 ^= tail[0];
                    k1 *= c1;
                    k1 = (k1 << 15) | (k1 >> (32 - 15));  // Left rotate by 15 bits
                    k1 *= c2;
                    h1 ^= k1;
        }
        h1 ^= len;
        h1 ^= h1 >> 16;
        h1 *= 0x85ebca6b;
        h1 ^= h1 >> 13;
        h1 *= 0xc2b2ae35;
        h1 ^= h1 >> 16;
        return h1;
    }

    // =====================================  E N D  ==========================================================
}
