class Solution {
    public:
        bool possible(vector<int>&nums, int val)
        {
            int n = sz(nums);
            v2d<int> dp(n+1,v1d<int>(val+1,-1));
            auto solve = [&](auto&&self,int i, int curr)->bool
            {
                if(i==n) return curr==0;
                if(curr==0) return true;
                if(dp[i][curr]!=-1) return dp[i][curr];
                bool pick = false, notPick = false;
                if(nums[i]<=curr) pick = self(self,i+1,curr-nums[i]);
                notPick = self(self,i+1,curr);
                return dp[i][curr] = pick or notPick;
            };
            return solve(solve,0,val);
        }
        int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
            int n = sz(nums), x = sz(q);
            int s = -1, e = x-1, ans = x;
            while(s<=e)
            {
                int m = s + (e-s)/2;
                vector<vector<vector<int>>> diff(n+1);
                rep(i,0,m+1,1) 
                    {
                diff[q[i][0]].pb({q[i][2],1});
                diff[q[i][1]+1].pb({q[i][2],-1});
}

                vector<vector<int>> changes(n);
                multiset<int> st;
                rep(i,0,n,1)
                {
                    iterate(it,diff[i])
                    {
                        if(it[1]==1) st.insert(it[0]); 
                        else remove(st,it[0]);
                    }
                    changes[i] = vector<int>(st.begin(),st.end());
                }
                bool check = true;
                for(int i=0;i<n;i++) check = check && possible(changes[i],nums[i]);
                if(check)
                {
                    e = m - 1;
                    ans = min(ans,m);
                }
                else s = m + 1;
            }
            return ans==x ? -1 : ans+1;
        }
    };



