class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        int l = 0, r = queries.size(), ans = -1;
        while(l <= r){
            int mid = (l + r) / 2;
            // mid = 4;
            // reduce by exactly val
            vector<vector<int>> v(n);
            for(int i = 0; i < mid; i++){
                int a = queries[i][0];
                int b = queries[i][1];
                int val = queries[i][2];
                for(int j = a; j <= b; j++){
                    v[j].push_back(val);
                }
            }
            bool ok = true;

            for(int i = 0; i < n; i++){
                vector<int> dp(nums[i] + 1);
                dp[0] = 1;
                // cout<<" i - "<<i<<" : ";
                for(auto aa : v[i]) {
                    // cout<<aa<<" ";
                    for(int j = nums[i] -1; j >=0; j--){
                        // cout<<j<<" "<<aa<<" "<<j + aa<<endl;
                        if(dp[j] == 0 || (j + aa) > nums[i])continue;

                        dp[j + aa] = 1;
                    }
                }
                // cout<<endl;
                if(dp[nums[i]] != 1){
                    ok = false;
                    break;
                }
            }
            // cout<<mid<<" "<<ok<<endl;
            if(ok){
                ans = mid;
                r = mid - 1;
            }
            else{
                l = mid + 1;
            }
            // break;
        }
        return ans;
    }
};

