class Solution {
public:
    bool helper(vector<int>& nums, vector<vector<int>>& q, int k) {
        int n = nums.size();
        for (int i=0; i < n; i++) {
            vector<int> temp;
            for (int j = 0; j < k; j++) {
                if (q[j][0] <= i && i <= q[j][1]) temp.push_back(q[j][2]);
            }
            int target = nums[i];
            vector<bool> dp(target + 1, false);
            dp[0] = true;
            for (int v : temp) {
                for (int s = target; s >= v; s--) 
                    dp[s] = dp[s] || dp[s - v];
                if (dp[target]) break;
            }
            if (!dp[target]) return false;
        }
        return true;
    }

    int minZeroArray(vector<int>& nums, vector<vector<int>>& q) {
        int n = q.size();
        for (int k = 0; k <= n; k++) {
            if (helper(nums, q, k)) return k;
        }
        return -1;
    }
};

