class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        best = [set([val]) for val in nums]
        for k, (l,r, val) in enumerate(queries):
            finish = True
            for i in range(len(best)):
                if 0 not in best[i]:
                    finish = False
            if finish:
                return k
            for i in range(l, r+1):
                best[i] = best[i]|{v-val for v in best[i] if v-val>=0}
            finish = True
            for i in range(len(best)):
                if 0 not in best[i]:
                    finish = False
            if finish:
                return k+1
        return -1

