class ZC {
public:
    bool chk(vector<int>& a) {
        return all_of(a.begin(), a.end(), [](int x) { return x == 0; });
    }
};

class BS {
public:
    int srch(vector<int>& a, vector<vector<int>>& q, function<bool(int)> vld) {
        int l = 0, r = q.size(), rs = -1;
        while (l <= r) {
            int m = (l + r) / 2;
            if (vld(m)) {
                rs = m;
                r = m - 1;
            } else {
                l = m + 1;
            }
        }
        return rs;
    }
};

class QP {
public:
    unordered_map<int, multiset<int>> get(vector<vector<int>>& q, int lim) {
        unordered_map<int, multiset<int>> mp;
        int j = 0;
        while (j < lim) {
            int l = q[j][0], r = q[j][1], v = q[j][2];
            int i = l;
            while (i <= r) {
                mp[i].insert(v);
                i++;
            }
            j++;
        }
        return mp;
    }
};

class SC {
public:
    bool can(int t, multiset<int>& v) {
        if (t == 0) return true;
        vector<bool> dp(t + 1, false);
        dp[0] = true;
        for (int x : v) {
            int j = t;
            while (j >= x) {
                if (dp[j - x]) dp[j] = true;
                j--;
            }
        }
        return dp[t];
    }
};

class VH {
public:
    function<bool(int)> vld(vector<int>& a, vector<vector<int>>& q, QP& p, SC& s) {
        return [&](int lim) {
            auto mp = p.get(q, lim);
            int i = 0;
            while (i < a.size()) {
                if (!s.can(a[i], mp[i])) return false;
                i++;
            }
            return true;
        };
    }
};

class Solution {
public:
    int minZeroArray(vector<int>& array, vector<vector<int>>& queries) {
        ZC zc;
        if (zc.chk(array)) return 0;

        QP qp;
        SC sc;
        VH vh;
        auto validator = vh.vld(array, queries, qp, sc);

        BS bs;
        return bs.srch(array, queries, validator);
    }
};

