func minZeroArray(nums []int, queries [][]int) int {
	solve := func(idx int) int {
		target := nums[idx]
		if target == 0 {
			return 0
		}

		dp := make([]bool, target+1)
		dp[target] = true

		for i, q := range queries {
			if q[0] <= idx && q[1] >= idx {
				newDp := make([]bool, target+1)
				for j := range target+1 {
					newDp[j] = dp[j]
					if j+q[2] <= target && dp[j+q[2]] {
						newDp[j] = dp[j+q[2]]
					}
				}
				dp = newDp
				if dp[0] {
					return i + 1
				}
			}
		}
		return -1
	}

	ans := 0
	for i := range nums {
		v := solve(i)
		if v == -1 {
			return -1
		}
		ans = max(ans, v)
	}
	return ans
}

