class Solution {
public:

    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n=nums.size(), m=queries.size(), ans=-1;
        if(*max_element(nums.begin(), nums.end())==0) return 0;
        vector<unordered_set<int>>dp(n);
        for(int i=0;i<n;i++){
            dp[i].insert(nums[i]);
        }
        for(int k=0;k<m;k++){
            vector<unordered_set<int>>next(n);
            int l=queries[k][0], r=queries[k][1], v=queries[k][2];
            for(int i=0;i<n;i++){
                if(dp[i].find(0)!=dp[i].end()){
                    next[i]=unordered_set<int>{0};continue;
                }
                if(l<=i&&i<=r){
                    for(auto a:dp[i]){
                        if(a>=v){
                            next[i].insert(a-v);
                        }
                        next[i].insert(a);
                    } 
                }
                else{
                    next[i]=dp[i];
                }

            }
            bool f=true;
            for(int i=0;i<n;i++){
                if(next[i].find(0)!=next[i].end()){
                    next[i]=unordered_set<int>{0};
                }
                else{
                    f=false;
                }
            }
            dp=next;
            if(f) return k+1;
        }
        return -1;

    }
};

