        // for(int i=0;i<n;i++)
        // {
        //     sum += temp[i];
        //     if(nums[i] > sum) return false;
        // }
        // return true;

        /*
            code only checking <= sum but in problem there is a fixed amount so
            sweep line is not possible
        */
        int n = nums.size();
        for(int i=0;i<n;i++)
        {
            int last = nums[i];
            vector<bool> dp(last + 1, false);
            dp[0] = true;
            for(int j=0;j<m;j++)
            {
                int start = queries[j][0];
                int end = queries[j][1];
                int value = queries[j][2];

                if(start <= i && i <= end)
                {
                    for(int k=last;k>=value;k--)
                    {
                        if(dp[k - value]) dp[k] = true;
                    }
                }
            }
            if(dp[last] == false) return false;
        }
        return true;
    }
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();

        vector<int> rem = nums;
        if(!isValid(rem, queries, m)) return -1;
        int start = 0;
        int end = m;
        while(start < end)
        {
            int mid = start + (end - start) / 2;
            if(isValid(rem, queries, mid)) end = mid;
            else start = mid + 1;
        }
        return end;
    }
};

