class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int ans = 0;

        for (int k=0; k<nums.size(); k++) {
            if (nums[k]==0)
                continue;
            vector<vector<bool>> dp(queries.size()+1, vector<bool>(nums[k]+1));
            int val = queries.size()+1;
            dp[0][0] = true;
            for (int i=1; i<=queries.size(); i++) {
                for (int j=nums[k]; j>=0; j--) {
                    if (j==0)
                        dp[i][j] = true;


                    dp[i][j] = dp[i-1][j] | dp[i][j];

                    if (k>=queries[i-1][0]&&k<=queries[i-1][1]&&j>=queries[i-1][2])
                        dp[i][j] = dp[i-1][j-queries[i-1][2]] | dp[i][j];

                    //cout << i << " " << j << " " << dp[i][j] << "n";
                    if (j == nums[k] && dp[i][j]) {
                        val = i;
                        break;
                    }
                }



                if (val < queries.size()+1)
                    break;
            }

            //cout << k << " " << val << "n";
            if (val == queries.size()+1)
                return -1;

            ans = max(ans, val);
        }

        return ans;
    }
};

