class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        // for each num
        // for each query, if included, try with and without decrementing
        int ans = -1;
        for (int i = 0; i < nums.size(); ++i) {
            if (nums[i] == 0) {
                ans = max(ans, 0);
                continue;
            }
            unordered_set<int> s = {nums[i]};
            int queryIndex = 0;
            for ( ; queryIndex < queries.size(); ++queryIndex) {
                auto& query = queries[queryIndex];
                if (i >= query[0] && i <= query[1]) {
                    vector<int> v(s.begin(), s.end());
                    for (int t : v) {
                        s.insert(t - query[2]);
                    }
                    if (s.find(0) != s.end()) {
                        break;
                    }
                }
            }
            if (s.find(0) == s.end()) {
                return -1;
            }
            ans = max(ans, queryIndex + 1);
        }
        return ans;
    }
};

