class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int n = nums.size();
        int m = queries.size();
        vector<vector<bool>> dp(n);
        vector<int> erl(n, -1);
        for(int j = 0; j < n; j++){
            dp[j].assign(nums[j] + 1, false);
            dp[j][0] = true;
            if(nums[j] == 0)
                erl[j] = 0;
        }
        for(int i = 0; i < m; i++){
            int l = queries[i][0];
            int r = queries[i][1];
            int v = queries[i][2];
            for(int j = l; j <= r; j++){
                if(erl[j] != -1)
                    continue;
                for(int s = nums[j] - v; s >= 0; s--){
                    if(dp[j][s] && s + v <= nums[j])
                        dp[j][s + v] = true;
                }
                if(dp[j][nums[j]])
                    erl[j] = i + 1;
            }
        }
        int ans = 0;
        for(int j = 0; j < n; j++){
            if(erl[j] == -1){
                return -1;
            }
            ans = max(ans, erl[j]);
        }
        return ans;
    }
};

