import java.util.*;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        int m = queries.length;
        List<List<Pair>> qList = new ArrayList<>();

        for (int i = 0; i < n; i++) {
            qList.add(new ArrayList<>());
        }

        for (int j = 0; j < m; j++) {
            int l = queries[j][0];
            int r = queries[j][1];
            int val = queries[j][2];
            for (int i = l; i <= r; i++) {
                qList.get(i).add(new Pair(j, val));
            }
        }

        int maxK = 0;
        for (int i = 0; i < n; i++) {
            int target = nums[i];
            if (target == 0) {
                maxK = Math.max(maxK, 0);
                continue;
            }

            List<Pair> qs = qList.get(i);
            int total = 0;
            for (Pair p : qs) {
                total += p.val;
            }
            if (total < target) {
                return -1;
            }

            Map<Integer, Integer> dp = new HashMap<>();
            dp.put(0, -1);
            for (Pair p : qs) {
                int j = p.index;
                int valj = p.val;
                Map<Integer, Integer> tmp = new HashMap<>();

                for (Map.Entry<Integer, Integer> entry : dp.entrySet()) {
                    int s = entry.getKey();
                    int currentJ = entry.getValue();
                    if (!tmp.containsKey(s) || currentJ < tmp.get(s)) {
                        tmp.put(s, currentJ);
                    }
                }

                for (Map.Entry<Integer, Integer> entry : dp.entrySet()) {
                    int s = entry.getKey();
                    int currentJ = entry.getValue();
                    if (s > target - valj) {
                        continue;
                    }
                    int newS = s + valj;
                    if (newS > target) {
                        continue;
                    }
                    int newJ = Math.max(currentJ, j);
                    if (!tmp.containsKey(newS) || newJ < tmp.get(newS)) {
                        tmp.put(newS, newJ);
                    }
                }

                dp = tmp;
            }

            if (!dp.containsKey(target)) {
                return -1;
            }
            int j_i = dp.get(target);
            maxK = Math.max(maxK, j_i + 1);
        }

        if (maxK == 0) {
            for (int num : nums) {
                if (num != 0) {
                    return -1;
                }
            }
            return 0;
        } else {
            return maxK;
        }
    }

    static class Pair {
        int index;
        int val;

        Pair(int index, int val) {
            this.index = index;
            this.val = val;
        }
    }
}

