    for(int i=1;i<=10;i++){
        for(int j=1;j<arr.size();j++){
            v[i][j]+=v[i][j-1];
        }
    }
    for(int i=0;i<arr.size();i++){
        if(arr[i]==0)continue;
        //now we have a set of decrements can we make arr[i] outof them
        int target = arr[i];
            vector<bool> dp(target + 1, false);
            dp[0] = true;
            for (int val = 1; val <= 10; val++) {
                int cnt = v[val][i];
                if (cnt == 0) continue;
                for (int t = target; t >= 0; t--) {
                    if (!dp[t]) continue;
                    for (int c = 1; c <= cnt && t + c * val <= target; c++) {
                        dp[t + c * val] = true;
                    }
                }
            }
            if (!dp[target]) return false;
    }
    return true;
}
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
