class Solution:
    def minZeroArray(self, nums: List[int], queries: List[List[int]]) -> int:
        n = len(nums)
        m = len(queries)

        dp = []
        for i in range(n):
            if nums[i] == 0:
                dp.append([True])
            else:
                dp.append([True] + [False] * nums[i])

        if all(x == 0 for x in nums):
            return 0

        for k in range(1, m + 1):
            l, r, val = queries[k - 1]  # k-th query (1-indexed k; queries are 0-indexed)
            for i in range(l, r + 1):
                if nums[i] > 0:
                    for s in range(nums[i] - val, -1, -1):
                        if dp[i][s]:
                            dp[i][s + val] = True
            all_zero = True
            for i in range(n):
                if nums[i] > 0 and not dp[i][nums[i]]:
                    all_zero = False
                    break
            if all_zero:
                return k
        return -1

