import java.util.*;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        // Check if all elements are zero
        boolean allZero = true;
        for (int num : nums) {
            if (num != 0) {
                allZero = false;
                break;
            }
        }
        if (allZero) {
            return 0;
        }

        int n = nums.length;
        List<List<Query>> coverage = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            coverage.add(new ArrayList<>());
        }

        // Populate coverage for each index
        for (int j = 0; j < queries.length; j++) {
            int l = queries[j][0];
            int r = queries[j][1];
            int val = queries[j][2];
            for (int i = l; i <= r; i++) {
                coverage.get(i).add(new Query(j, val));
            }
        }

        int maxK = -1;

        for (int i = 0; i < n; i++) {
            int target = nums[i];
            if (target == 0) {
                continue;
            }

            List<Query> qList = coverage.get(i);
            if (qList.isEmpty()) {
                return -1;
            }

            Set<Integer> possibleSums = new HashSet<>();
            possibleSums.add(0);
            boolean found = false;

            for (Query q : qList) {
                Set<Integer> newSums = new HashSet<>(possibleSums);
                for (int s : possibleSums) {
                    newSums.add(s + q.val);
                }
                possibleSums = newSums;

                if (possibleSums.contains(target)) {
                    if (q.originalIndex > maxK) {
                        maxK = q.originalIndex;
                    }
                    found = true;
                    break;
                }
            }

            if (!found) {
                return -1;
            }
        }

        if (maxK == -1) {
            return 0;
        }

        return maxK + 1;
    }

    private static class Query {
        int originalIndex;
        int val;

        Query(int o, int v) {
            originalIndex = o;
            val = v;
        }
    }
}

