class Solution {
public:

bool isPossible(vector<int>& nums, vector<vector<int>>& queries, int k) {
    int n = nums.size();
    for(int i = 0; i < n; ++i) {
        vector<int> vals;
        for(int q = 0; q < k; ++q) {
            int l = queries[q][0], r = queries[q][1], val = queries[q][2];
            if(l <= i && i <= r) vals.push_back(val);
        }

        vector<bool> dp(nums[i] + 1, false);
        dp[0] = true;
        for(int v : vals) {
            for(int x = nums[i]; x >= v; --x) {
                if(dp[x - v]) dp[x] = true;
            }
        }
        if(!dp[nums[i]]) return false; 
    }
    return true;
}

int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
    int lo = 0, hi = queries.size(), ans = -1;
    while(lo <= hi){
        int mid = (lo + hi)/2;
        if(isPossible(nums, queries, mid)){
            ans = mid;
            hi = mid - 1;
        } else {
            lo = mid + 1;
        }
    }
    return ans;
}

};

