        need = 0
        for i, num in enumerate(nums):
            @cache
            def best(qidx, target):
                if target == 0:
                    return qidx
                if qidx == len(queries):
                    return inf
                if target < 0:
                    return inf
                res = best(qidx + 1, target)
                l, r, val = queries[qidx]
                if l <= i <= r and target >= val:
                    res = min(best(qidx + 1, target - val), res)
                return res
            need = max(best(0, num), need)
            best.cache_clear()
        return need if need != inf else -1

