class Solution {
public:
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int res = 0;
        int n = nums.size();
        for(int i = 0; i < n; i ++) {
            int k = solve(nums[i], i, queries);
            if(k == -1) {
                return -1;
            }
            res = max(res, k);
        }
        return res;
    }

    int solve(int num, int i, vector<vector<int>>& queries) {
        if(num == 0) return 0;
        int m = queries.size();
        vector<bool> dp(num + 1, false);
        dp[0] = true;
        for(int j = 0; j < m; j ++) {
            int l = queries[j][0], r = queries[j][1], val = queries[j][2];
            if(i < l || i > r || val > num) continue;
            for(int k = num; k >= val; k --) {
                dp[k] = dp[k] || dp[k - val];
            }
            if(dp[num]) {
                return j + 1;
            }
        }
        return -1;
    }
};

