class Solution {
public:
    bool subsetSum(const vector<int>& arr, int target) {
        int total = 0;
        for (int x : arr)
            total += x;
        if (total < target)
            return false;
        vector<bool> dp(target + 1, false);
        dp[0] = true;
        for (int x : arr) {
            for (int j = target; j >= x; j--) {
                dp[j] = dp[j] || dp[j - x];
            }
        }
        return dp[target];
    }
    int minZeroArray(vector<int>& nums, vector<vector<int>>& queries) {
        int m = queries.size();
        int n = nums.size();
        int low = 0, high = m, ans = -1;

        while (low <= high) {
            int mid = low + (high - low) / 2;
            vector<vector<int>> diff(queries.begin(), queries.begin() + mid);
            bool possible = true;
            for (int i = 0; i < n; i++) {
                vector<int> available;
                for (int j = 0; j < mid; j++) {
                    int l = diff[j][0], r = diff[j][1], val = diff[j][2];
                    if (i >= l && i <= r) {
                        available.push_back(val);
                    }
                }
                if (!subsetSum(available, nums[i])) {
                    possible = false;
                    break;
                }
            }
            if (possible) {
                ans = mid;
                high = mid - 1; 
            } else {
                low = mid + 1;
            }
        }
        return ans;
    }
};

