class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        BitSet[] dp = new BitSet[n];

        for (int j = 0; j < n; j++) {
            dp[j] = new BitSet(1001);
            dp[j].set(0);
        }

        boolean allZero = true;
        for (int num : nums) {
            if (num != 0) {
                allZero = false;
                break;
            }
        }
        if (allZero) return 0;

        for (int k = 0; k < queries.length; k++) {
            int l = queries[k][0];
            int r = queries[k][1];
            int v = queries[k][2];

            for (int j = l; j <= r; j++) {
                BitSet temp = new BitSet(1001);
                for (int pos = dp[j].nextSetBit(0); pos >= 0 && pos + v < 1001; pos = dp[j].nextSetBit(pos + 1)) {
                    temp.set(pos + v);
                }
                dp[j].or(temp);

                for (int pos = nums[j] + 1; pos < 1001; pos++) {
                    dp[j].clear(pos);
                }
            }

            boolean allAchieved = true;
            for (int j = 0; j < n; j++) {
                if (!dp[j].get(nums[j])) {
                    allAchieved = false;
                    break;
                }
            }
            if (allAchieved) return k + 1;
        }
        return -1;
    }
}

