import java.util.*;

class Solution {
    public int minZeroArray(int[] nums, int[][] queries) {
        int n = nums.length;
        BitSet[] bitMaskArray = new BitSet[n];

        // Initialize each BitSet and set bit 0 to true
        for (int i = 0; i < n; i++) {
            bitMaskArray[i] = new BitSet(1001);
            bitMaskArray[i].set(0);
        }

        // Check if all elements in nums are already zero
        boolean allElementsZero = true;
        for (int num : nums) {
            if (num != 0) {
                allElementsZero = false;
                break;
            }
        }
        if (allElementsZero) return 0;

        // Process each query
        for (int k = 0; k < queries.length; k++) {
            int left = queries[k][0];
            int right = queries[k][1];
            int value = queries[k][2];

            for (int i = left; i <= right; i++) {
                BitSet shifted = new BitSet(1001);

                // Manual left shift (BitSet does not support `<<`)
                for (int bit = bitMaskArray[i].nextSetBit(0); bit >= 0; bit = bitMaskArray[i].nextSetBit(bit + 1)) {
                    if (bit + value < 1001) {
                        shifted.set(bit + value);
                    }
                }

                bitMaskArray[i].or(shifted);

                // Reset unreachable values
                for (int pos = nums[i] + 1; pos < 1001; pos++) {
                    bitMaskArray[i].clear(pos);
                }
            }

            // Check if all target values are reached
            boolean allValuesReached = true;
            for (int i = 0; i < n; i++) {
                if (!bitMaskArray[i].get(nums[i])) {
                    allValuesReached = false;
                    break;
                }
            }

            if (allValuesReached) return k + 1;
        }

        return -1;
    }
}

