            int v = queries[k][2];

            for (int j = l; j <= r; j++) {
                BitSet shifted = new BitSet(1001);

                // Perform bit shifting manually
                for (int i = dp[j].nextSetBit(0); i >= 0 && i + v < 1001; i = dp[j].nextSetBit(i + 1)) {
                    shifted.set(i + v);
                }

                dp[j].or(shifted);

                // Clear bits greater than nums[j]
                for (int pos = nums[j] + 1; pos < 1001; pos++) {
                    dp[j].clear(pos);
                }
            }

            // Check if nums[j] is achievable in dp[j]
